package algos.hackerrank.medium.abbreviation;

import java.util.*;

public class Abbreviation {

  private static final Set<List<String>> cache = new HashSet<>();

  public static String abbreviation1(String a, String b) {
    String result = aToB(a, b);
    return result;
  }

  public static String aToB(String a, String b) {
    List<String> aAndB = new ArrayList<>();
    aAndB.add(a);
    aAndB.add(b);
    if (a.equals(b) || a.toUpperCase().equals(b)) {
      return "YES";
    }
    if (cache.contains(aAndB) || a.isEmpty() && !b.isEmpty()) {
      cache.add(aAndB);
      return "NO";
    } else if (b.isEmpty() && a.isEmpty()) {
      return "YES";
    } else if (b.isEmpty()) {
      for (int i = 0; i < a.length(); i++) {
        if (Character.isUpperCase(a.charAt(i))) {
          cache.add(aAndB);
          return "NO";
        }
      }

      return "YES";
    } // A A
    if (a.charAt(0) == b.charAt(0) && Character.isUpperCase(a.charAt(0))) {
      String result = aToB(a.substring(1), b.substring(1));
      if (result.equals("YES")) {

        return "YES";
      } else {
        cache.add(aAndB);
        return "NO";
      }
    } else if (Character.isUpperCase(a.charAt(0)) && Character.isLowerCase(b.charAt(0))) {
      cache.add(aAndB);
      return "NO";
    }
    // a B
    else if (Character.isLowerCase(a.charAt(0))
        && Character.isUpperCase(b.charAt(0))
        && Character.toUpperCase(a.charAt(0)) != b.charAt(0)) {
      return aToB(a.substring(1), b);
    }
    // a a, a A
    else if (a.charAt(0) == b.charAt(0) && Character.isLowerCase(a.charAt(0))
        || Character.isLowerCase(a.charAt(0))
            && Character.toUpperCase(a.charAt(0)) == b.charAt(0)) {
      String s1 = aToB(a.substring(1), b.substring(1));
      if (s1.equals("YES")) {
        {
          return "YES";
        }
      } else {
        cache.add(aAndB);
        return aToB(a.substring(1), b);
      }

      // A a
    } else {
      cache.add(aAndB);
      return "NO";
    }
  }

  public static void main(String[] args) {

    /*System.out.println(abbreviation1("", ""));
     System.out.println(abbreviation1("", ""));
     System.out.println(abbreviation1("", ""));
    */
    System.out.println(
        abbreviation1(
            "",
            ""));
    /*System.out.println(abbreviation1("", ""));

        System.out.println(abbreviation1("", ""));
        System.out.println(abbreviation1("", ""));
        System.out.println(abbreviation1("", ""));
        System.out.println(abbreviation1("", ""));
        System.out.println(abbreviation1("", ""));
    */
  }
}

